$(document).ready(function(){$("#datatableleads tbody tr:first-child th").each(function(){var t=$(this).text();$(this).html('<input type="text" placeholder="Search '+t+'" />')}),$("#datatableleads").DataTable({initComplete:function(){this.api().columns().every(function(){var t=this;$("input").on("keyup change clear",function(){t.search()!==this.value&&t.search(this.value).draw()})})}})});